var sound=new Audio("alert.mp3"),refreshAlerts=0,refreshHands=0,timeoutOne,timeoutTwo,totalTimeoutValue,clockRunning=false,currentTime=0,seconds,minutes,hour,secondHand=document.getElementsByClassName("seconds")[0],minuteHand=document.getElementsByClassName("minutes")[0],hourHand=document.getElementsByClassName("hours")[0],timeoutOneInput=document.getElementsByClassName("timeoutOne")[0],timeoutTwoInput=document.getElementsByClassName("timeoutTwo")[0],startButton=document.getElementsByClassName("start")[0],resetButton=document.getElementsByClassName("reset")[0];function takeBrake(){sound.play();alert("Take a brake ^^")}function goStudy(){sound.play();alert("Time to study..")}function startClock(){if(timeoutOneInput.value==""||timeoutTwoInput.value==""){return false}if(clockRunning){return false}else{clockRunning=true}timeoutOneValue=timeoutOneInput.value*60000;timeoutTwoValue=timeoutTwoInput.value*60000;totalTimeoutValue=parseInt(timeoutOneValue,10)+parseInt(timeoutTwoValue,10);timeoutOne=setTimeout(takeBrake,timeoutOneValue);timeoutTwo=setTimeout(goStudy,totalTimeoutValue);refreshAlerts=setInterval(function(){timeoutOne=setTimeout(takeBrake,timeoutOneValue);timeoutTwo=setTimeout(goStudy,totalTimeoutValue)},totalTimeoutValue)}function resetClock(){clockRunning=false;clearTimeout(timeoutOne);clearTimeout(timeoutTwo);clearInterval(refreshAlerts);clearInterval(refreshHands)}startButton.addEventListener("click",startClock,false);resetButton.addEventListener("click",resetClock,false);function updateClock(){currentTime=new Date(),seconds=currentTime.getSeconds()*6,minutes=currentTime.getMinutes()*6,hours=(currentTime.getHours()*30)+(currentTime.getMinutes()/2);secondHand.style.WebkitTransform="rotate("+seconds+"deg)";secondHand.style.transform="rotate("+seconds+"deg)";minuteHand.style.WebkitTransform="rotate("+minutes+"deg)";minuteHand.style.transform="rotate("+minutes+"deg)";hourHand.style.WebkitTransform="rotate("+hours+"deg)";hourHand.style.transform="rotate("+hours+"deg)"}var startClock=setInterval(function(){updateClock()},1000);